package gipsy.GIPC.imperative;


/**
 * Enumeration of supported imperative languages.
 * Eventually, should be generated by some means.
 *
 * $Id: EImperativeLanguages.java,v 1.9 2005/06/25 00:47:02 mokhov Exp $
 *
 * @author Serguei Mokhov, mokhov@cs.concordia.ca
 * @version $Revision: 1.9 $
 * @since 1.0.0
 */
public interface EImperativeLanguages
{
	int JAVA    = 0;
	int C       = 1;
	int CPP     = 2;
	int PERL    = 3;
	int PYTHON  = 4;
	int FORTRAN = 5;

	/**
	 * Must correspond to the order above.
	 * These will be matched to the code segments
	 * within GIPSY program.
	 */
	String[] IMPERATIVE_LANGUAGES =
	{
		"JAVA",
		"C",
		"CPP",
		"PERL",
		"PYTHON",
		"FORTRAN"
	};

	/**
	 * Must correspond to the order above.
	 * These will be matched to the main
	 * compiler classes implementing IImperativeCompiler.
	 */
	String[] IMPERATIVE_COMPILERS =
	{
		"gipsy.GIPC.imperative.Java.JavaCompiler",
		"gipsy.GIPC.imperative.C.CCompiler",
		"gipsy.GIPC.imperative.Cpp.CPPCompiler",
		"gipsy.GIPC.imperative.Perl.PerlCompiler",
		"gipsy.GIPC.imperative.Python.PythonCompiler",
		"gipsy.GIPC.imperative.Fortran.FortranCompiler"
	};
}

