package gipsy.GIPC.intensional;

/**
 * Enumeration of supported intensional languages.
 * Eventually, should be generated by some means.
 *
 * @author Serguei Mokhov
 * @version $Id: EIntensionalLanguages.java,v 1.15 2013/03/12 17:07:04 mokhov Exp $
 * @since 1.0.0
 */
public interface EIntensionalLanguages
{
	int GIPL            = 0;
	int INDEXICAL_LUCID = 1;
	int JLUCID          = 2;
	int OBJECTIVE_LUCID = 3;
	int TENSOR_LUCID    = 4;
	int ONYX            = 5;
	int GRANULAR_LUCID  = 6;
	int LUCX            = 7;
	int JOOIP           = 8;
	int FORENSIC_LUCID  = 9;
	int TRANSLUCID      = 10;

	/**
	 * String representation of the enumeration.
	 * Must correspond to the order above.
	 * These will be matched to the code segments
	 * within a GIPSY program.
	 */
	String[] INTENSIONAL_LANGUAGES =
	{
		"GIPL",
		"INDEXICALLUCID",
		"JLUCID",
		"OBJECTIVELUCID",
		"TENSORLUCID",
		"ONYX",
		"GLU",
		"LUCX",
		"JOOIP",
		"FORENSICLUCID",
		"TRANSLUCID"
	};

	/**
	 * Must correspond to the order above.
	 * These will be matched to the main
	 * compiler classes implementing IIntensionalCompiler.
	 */
	String[] INTENSIONAL_COMPILERS =
	{
		"gipsy.GIPC.intensional.GIPL.GIPLCompiler",
		"gipsy.GIPC.intensional.SIPL.IndexicalLucid.IndexicalLucidCompiler",
		"gipsy.GIPC.intensional.SIPL.JLucid.JLucidCompiler",
		"gipsy.GIPC.intensional.SIPL.ObjectiveLucid.ObjectiveLucidCompiler",
		"gipsy.GIPC.intensional.SIPL.TensorLucid.TensorLucidCompiler",
		"gipsy.GIPC.intensional.SIPL.Onyx.OnyxCompiler",
		"gipsy.GIPC.intensional.SIPL.GLU.GLUCompiler",
		"gipsy.GIPC.intensional.SIPL.Lucx.LucxCompiler",
		"gipsy.GIPC.intensional.SIPL.JOOIP.JOOIPCompiler",
		"gipsy.GIPC.intensional.SIPL.ForensicLucid.ForensicLucidCompiler",
		"TransLucid Compiler Does not Exist Yet"
	};
}

